fun main(args: Array<String>) {
    if (args.size != 1) return
    var floor: Int = 0;
    var below: Int = -1;

    for(i in instructions.indices) {
        if(instructions[i].equals('(')) {
            floor++;
        } else if(instructions[i].equals(')')){
            floor--;
        }

        if(floor < 0 && below == -1) {
            below = i;
        }
    }

    println("Santa went below ground level on step $below");
    println("Santa went to floor $floor");
}